mapper.select:
select * from(
SELECT aa.*,rownum rnum FROM (
select bpayment.bpaynum ordernum,borderdate,bfinalmoney,delfee,bstatus,mname,receiver from
(select TO_DATE(borderdate,'YY/MM/DD') borderdate,bpaynum,bfinalmoney,delfee,bstatus,receiver,mnum from bpayment where mnum=#{mnum} and 
TO_CHAR(BORDERDATE,'YY/MM/DD') >= TO_CHAR(TO_DATE(#{startDay},'YY/MM/DD'),'YY/MM/DD') AND TO_CHAR(BORDERDATE,'YY/MM/DD')<= TO_CHAR(TO_DATE(#{endDay},'YY/MM/DD'),'YY/MM/DD')
 )bpayment,						
(select * from members)members
where members.mnum=bpayment.mnum
order by borderdate desc
) aa
)
where rnum>=#{startRow} and rnum<=#{endRow}
