mapper.select:
select btitle,book.bnum,bprice,bpoint, imgsavefilename,cartNum,cart.bcount from (select * from cart where btype=1 and cartnum in
#{item}
)cart, (select * from books) book, (select * from img where THUMBNAIL=1)img where cart.bnum=book.bnum and book.bnum=img.bnum

select btitle,book.bnum,bprice,bpoint,imgsavefilename from
(select * from books) book,(select * from img where thumbnail=1)img
where book.bnum=img.bnum and book.bnum=#{bnum}
